import React, { useState } from 'react';
import { TextField, Button, CssBaseline, Paper, Box, Grid, Typography } from '@mui/material';

import { createTheme, ThemeProvider } from '@mui/material/styles';
import MyLogo from '../../img/TSO-AOV1.png';
import Divider from '@mui/material/Divider';
import { useMsal } from '@azure/msal-react';
import { loginRequest } from '../../auth/authConfig';
import Poster from '../../img/S__7700483.jpg';


const defaultTheme = createTheme();
const LoginPage = () => {
  const { instance } = useMsal();
  const [passwordError, setPasswordError] = useState(false);
  const [usernameError, setUsernameError] = useState(false);
  const CheckformLogin = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    const username = data.get('username');
    const password = data.get('password');
    if (!username.trim() && !password.trim()) {
      setUsernameError(true);
      setTimeout(() => { setUsernameError(false); }, 2000);
      setPasswordError(true);
      setTimeout(() => { setPasswordError(false); }, 2000);
      return;
    }
    if (!password.trim()) {
      setPasswordError(true);
      setTimeout(() => { setPasswordError(false); }, 2000);
      return;
    }
    if (!username.trim()) {
      setUsernameError(true);
      setTimeout(() => { setUsernameError(false); }, 2000);
      return;
    }
  };

  const handleLoginSSO = () => {
    instance.loginPopup(loginRequest).catch(e => {
      console.error(e);
    });
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Grid container component="main" sx={{ height: '100vh', display: 'flex' }}>
        <CssBaseline />
        <Grid item xs={12} sm={4} md={7} sx={{
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          overflow: "hidden"
        }}
        >
          <img src={`${Poster}?t=${new Date().getTime()}`} alt="Poster" style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
          }}
          />
        </Grid>
        <Grid
          item
          xs={12}
          sm={8}
          md={5}
          component={Paper}
          elevation={6}
          square
          sx={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            height: '100%',
            p: { xs: 2, md: 4 }
          }}
        >
          <Box
            sx={{
              my: 5,
              mx: 4,
              flexDirection: 'column',
              width: { xs: '100%', sm: 'auto' },
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            <img src={`${MyLogo}?t=${new Date().getTime()}`} alt="Logo" style={{ width: '100%', maxWidth: '300px', height: 'auto' }} />

            <Box component="form" noValidate onSubmit={CheckformLogin} sx={{ mt: 1, width: '100%' }}>
              <Typography
                variant="body1"
                sx={{ fontWeight: 'bold', mb: -2, mt: 2, color: 'gray' }}
              >
                Login with Microsoft
              </Typography>
              <Button
                startIcon={<img
                  src="data:image/png;base64,"
                  style={{ width: '25px', height: '25px', verticalAlign: 'middle' }}
                  alt="Microsoft"
                />}
                fullWidth
                variant="outlined"
                size="large"
                sx={{
                  mt: 3,
                  mb: 2,
                  '&:hover': {
                    bgcolor: 'primary.dark',
                    color: '#fff'
                  },
                  fontSize: '16px',

                  fontWeight: 'bold',
                }}
                onClick={handleLoginSSO}
              >
                SIGN IN WITH MICROSOFT SSO
              </Button>
              <Divider>OR</Divider>
              <Typography
                variant="body2"
                sx={{ fontWeight: 'bold', mt: 2, color: 'gray' }}
              >
                External Login
              </Typography>
              <TextField
                error={usernameError}
                margin="normal"
                required
                fullWidth
                id="username"
                label="Username"
                name="username"
                autoComplete="username"
                autoFocus
                disabled
              />
              <TextField
                error={passwordError}
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                disabled
              />
              <Button
                disabled
                type="submit"
                fullWidth
                variant="contained"
                sx={{
                  mt: 3,
                  mb: 2,
                  bgcolor: 'green',
                  '&:hover': {
                    bgcolor: 'green',
                  },
                  color: 'white',
                  fontSize: '16px',
                  fontWeight: 'bold',
                }}
              >
                Sign In
              </Button>
              <Typography variant="body2" style={{ fontSize: '1rem', fontWeight: 'bold', color: 'gray' }} align="center">
                <br />ติดต่อสอบถามรายละเอียดได้ที่ คป.<br /> คุณปีติภัทร โทร 35266 คุณอธิเบศร์ โทร 35259 คุณปกป้อง โทร 35395<br />
                <b className="text-secondary">Copyright © 2025 PTT Public Company Limited. All rights reserved.</b><br />
                <img
                  src="data:image/png;base64,"
                  style={{ width: '27px', height: '27px', verticalAlign: 'middle' }}
                  alt="Microsoft Edge"
                />
                <b className="text-secondary"> Microsoft Edge, </b>
                <img
                  src="data:image/png;base64,"
                  style={{ width: '27px', height: '27px', verticalAlign: 'middle' }}
                  alt="Google Chrome"
                />
                <b className="text-secondary"> Google Chrome, </b>
                <img
                  src="data:image/png;base64,"
                  style={{ width: '27px', height: '27px', verticalAlign: 'middle' }}
                  alt="Mozilla Firefox"
                />
                <b className="text-secondary"> Mozilla Firefox and </b>
                <img
                  src="data:image/png;base64,"
                  style={{ width: '27px', height: '27px', verticalAlign: 'middle' }}
                  alt="Safari"
                />
                <b className="text-secondary"> Safari</b>
              </Typography>
            </Box>
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}

export default LoginPage;
